mapper.select:
SELECT id, owner_name, content, content_meta, image_url, created_date, updated_date, user_id, board_id
FROM paper
where id = #{paperId}

SELECT id, owner_name, content, content_meta, image_url, created_date, updated_date, user_id, board_id
FROM paper
where user_id = #{userId}
ORDER BY id
LIMIT #{page}

SELECT id, owner_name, content, content_meta, image_url, created_date, updated_date, user_id, board_id
FROM paper
where board_id = #{boardId}
ORDER BY id
LIMIT #{page}
